import {Component} from 'preact'

class Questionnaire extends Component {
	/**
	 *
	 * @param {preact.ComponentProps} props
	 */
	constructor(props) {
		super(props)
		this.state = {showQuestionnaire: false}
	}

	/**
	 * Used to toggle a questionnaire
	 * @param {Boolean} showQuestionnaire whether to show the questionnaise
	 */
	toggleQuestionnaire(showQuestionnaire = false) {
		return () => this.setState({showQuestionnaire})
	}
}

export default Questionnaire

import {h, Component} from 'preact'
import qs from 'qs'
import {route} from 'preact-router'
import M from 'materialize-css'

import PatientDemographicInfo from './Patient'
import PatientHistoryInfo from './History'
import ContactInfo from './Contact'

import {Loader} from '../../Partial'
import {fhirBase, doModal, getJwtPayload} from '../../util'

import '../../styles/create-patient.scss'

function createHistory(patient_id) {
	const elems = document.querySelectorAll('.patient-history-input')
	const form = [...elems].reduce((acc, elem) => {
		// dataset should be of form '$KEY-attr, eg health-childhood-illnesses
		const [key, ...rest] = elem.dataset.formKey.split('-')
		const attr = rest.join('-')
		// ensure accumulator has form {health: {}, drugs: {}}
		if (!(key in acc)) acc[key] = {}
		// get handle on current place in data structure
		const cur = acc[key]
		// fetch data from element
		switch (elem.dataset.materializeType) {
		// use materialize method to get selected values
		case 'select': {
			const inst = M.FormSelect.getInstance(elem)
			cur[attr] = inst.getSelectedValues()
			return acc
		}
		// materialize requires a lot of markup for a radio button (form>p>label>input)
		// find the checked radio button and get the val (true/false - set in elem.value prop)
		case 'radio-group': {
			const checked = elem.querySelector(':checked')
			cur[attr] = checked.value.replace('yes', 'true').replace('no', 'false')
			return acc
		}
		// saved for when there's the option to add/remove
		case 'input-group': {
			const inputs = elem.querySelectorAll('input')
			// get all non-null values from inputs in the group
			cur[attr] = [...inputs].map(el => el.value.trim()).filter(Boolean)
			return acc
		}
		case 'multiple-input-group': {
			const inputs = elem.querySelectorAll('.input-group')
			cur[attr] = [...inputs].map(input => ({
				name: input.querySelector('.name').value,
				dose: input.querySelector('.dose').value,
				freq: input.querySelector('.frequency').value,
			}))
			return acc
		}
		default: {
			cur[attr] = elem.value ? elem.value.trim() : null
			return acc
		}
		}
	}, {})
	const sigCanv = document.getElementById('sign-off-canvas')
	const rawImg = sigCanv.toDataURL('image/png')
	form.sign.image = rawImg
	form.patient_id = patient_id
	form.sign.practitioner_id = getJwtPayload(localStorage.token).userid
	return fhirBase.post('/History', form, {
		headers: {'content-type': 'application/json'},
	})
}

class AdmitPatient extends Component {
	/**
	 * Create Patient component
	 * @param {object} props component props
	 */
	constructor(props) {
		super(props)
		this.state = {
			wards: [],
			loaded: false,
		}

		this.getImg = this.getImg.bind(this)
		this.setImg = this.setImg.bind(this)
		this.setVideo = this.setVideo.bind(this)
		this.setCanvas = this.setCanvas.bind(this)
	}

	/**
	 * Gets all locations from the API and populates state
	 */
	async componentDidMount() {
		const resp = await fhirBase.get('/Location?type=Ward')
		if (resp.data) {
			this.setState({
				loaded: true,
				wards: resp.data.map(ward => ({val: ward.id, text: ward.name})),
			}, async () => {
				// the form is showing and webcam is, so populate with webcam
				const select = document.querySelectorAll('#location_id, #patient-gender, .patient-details-select')
				M.FormSelect.init(select)
				try {
					const stream = await navigator.mediaDevices.getUserMedia({video: true})
					this.video.srcObject = stream
					this.video.onloadedmetadata = this.video.play
					// when the video plays, the height and width of the container changes
					// set the canvas here
					this.video.addEventListener('playing', () => {
						const dimensions = this.video.getBoundingClientRect()
						this.canvas.height = dimensions.height
						this.canvas.width = dimensions.width
					})
				} catch (err) {
					M.toast({html: 'There was an error initialising the Webcam'})
					console.error(`Webcam error: ${err}`)
				}
				const date = document.querySelectorAll('.datepicker')
				M.Datepicker.init(date, {autoClose: true})
			})
		}
	}

	/**
	 * Takes webcam piped to video and sticks on canvas
	 * Saves this to B64, sets state and pauses the stream
	 */
	getImg() {
		console.log('[CREATE] Saving image')
		const dimensions = this.video.getBoundingClientRect()
		this.canvas.getContext('2d').drawImage(this.video, 0, 0, dimensions.width, dimensions.height)
		const img = this.canvas.toDataURL('image/png')
		this.img = img
		doModal('Success', 'Image saved')
		this.video.pause()
	}

	/**
	 * serialise the uploaded image and save it
	 * @param {EventTarget} ev Event from input onChange event
	 */
	setImg(ev) {
		try {
			const {files: [file]} = ev.target
			const reader = new FileReader()
			reader.addEventListener('load', () => this.img = reader.result, false)
			reader.readAsDataURL(file)
		} catch (err) {
			doModal('Error', `There was an error setting the image: ${err}`)
		}
	}


	setVideo(ref) {
		this.video = ref
	}

	setCanvas(ref) {
		this.canvas = ref
	}

	/**
	 * yanks all data from form and posts to API
	 * creates a patient and then an encounter
	 */
	async admit() {
		const form = new FormData()
		if (this.img) {
			console.log('[CREATE] Appending image')
			const img = await fetch(this.img).then(r => r.blob())
			form.append('patient-photo', img)
		}
		const labels = [
			'patient-prefix',
			'patient-given',
			'patient-family',
			'patient-gender',
			'location_id',
			'contact-prefix',
			'contact-given',
			'contact-family',
			'contact-fullname',
			'contact-phone',
		]
		const invalid = []
		const obj = labels.reduce((acc, label) => {
			const elem = document.getElementById(label)
			const {value} = elem
			if (value) {
				acc[label] = value
				elem.classList.add('valid')
				elem.classList.remove('invalid')
			} else {
				invalid.push(label)
				elem.classList.add('invalid')
				elem.classList.remove('valid')
			}
			return acc
		}, {})
		if (invalid.length) {
			const err = invalid
				.map(la => la.replace(/-/g, ' '))
				.map(la => la.split(' ').map(word => word.charAt(0).toUpperCase() + word.slice(1)).join(' '))
				.map(la => `<li>${la}</li>`)
				.join('\n')
			doModal('Error with form!', `Please complete the following fields: <ul>${err}</ul>`)
			return
		}
		obj['patient-fullname'] = obj['patient-given'] + obj['patient-family']

		Object.keys(obj).forEach(label => form.append(label, obj[label]))
		try {
			const resp = await fhirBase.post('/Patient', form)
			const {issue: [outcome]} = resp.data
			if (outcome.code === 200) {
				const encForm = new FormData()
				encForm.append('class', 'admission')
				encForm.append('status', 'finished')
				encForm.append('patient_id', outcome.diagnostics.patient_id)
				encForm.append('location_id', obj.location_id)
				const encResp = await fhirBase.post('/Encounter', encForm)
				const histResp = await createHistory(outcome.diagnostics.patient_id)
				console.log(histResp)
				doModal('Success', encResp.data.issue[0].details.text)
			} else {
				doModal('Error', outcome.data.issue[0].details.text)
			}
		} catch (err) {
			console.warn('[create patient]', err)
			doModal('Error', `There is an error with patient creation: ${err}`)
		}
	}


	/**
	 * renders component
	 * if no wards, render a loading icon
	 * @returns {VNode}
	 */
	render() {
		if (!this.state.loaded) return <Loader />
		return (
			<div className="row" id="patient-form">
				<h2>Admit a New Patient</h2>
				<form>
					<PatientDemographicInfo
						wards={this.state.wards}
						getImg={this.getImg}
						setImg={this.setImg}
						setVideo={this.setVideo}
						setCanvas={this.setCanvas}
						playVideo={() => this.video.play()}
					/>
					<PatientHistoryInfo />
					<ContactInfo />
					<a className="waves-effect waves-light btn col s12" onClick={this.admit.bind(this)}>
						<i className="material-icons left">perm_identity</i>Admit
					</a>
				</form>
			</div>
		)
	}
}


export default AdmitPatient

import {h, Component} from 'preact'
import M from 'materialize-css'

import {fhirBase, doModal} from '../util'

import '../styles/create-location.scss'

class CreateWard extends Component {
	/**
	 * Ensure elements have some content and change their class accordingly
	 * @param {Array<VNode>} inputs Inputs to validate .value
	 * @return {boolean} Whether the given inputs are valid
	 */
	static validateForms(inputs) {
		// give empty fields a red box, remove valid classname too
		const invalid = inputs.filter((control) => {
			if (!control.value) {
				control.classList.add('invalid')
				control.classList.remove('valid')
			}
			return !control.value
		})

		// give all valid ones the green box, removing the red
		inputs.forEach((control) => {
			if (!invalid.includes(control)) {
				control.classList.remove('invalid')
				control.classList.add('valid')
			}
		})

		// length === 0 if all inputs have some data. cast this to boolean
		return !invalid.length
	}

	/**
	 * Page component used to create locations by interacting with /fhir/Location API
	 * @param {object} props Props given to the preact component
	 */
	constructor(props) {
		super(props)
		this.instances = []
	}

	/**
	 * Initialise all materialize elements on page load
	 */
	componentDidMount() {
		this.instances.push(
			M.CharacterCounter.init(this.name),
			M.CharacterCounter.init(this.desc),
		)
	}

	/**
	 * Validate the elements on the page and then POST their data to /fhir/Location
	 * @param {Event} ev Click event
	 * @returns {Promise<void>}
	 */
	async makeWard(ev) {
		ev.preventDefault() // don't refresh the page - this is a SPA!
		const inputs = [this.name, this.desc] // inputs to check
		const valid = CreateWard.validateForms(inputs)
		if (!valid) return // not valid. let the classNames do the talking

		// populate a form and send it to the server
		const locForm = new FormData()
		locForm.append('type', 'ward')
		inputs.forEach(control => locForm.set(control.id, control.value))

		// attempt to post
		try {
			const resp = await fhirBase.post('/Location', locForm)
			const {id} = resp.data.issue[0].diagnostics
			doModal('Success', `Successfully created "${this.name.value}" with ID ${id}`)
			inputs.forEach(input => input.value = '')
		} catch (err) {
			doModal('Error!', `There was an error whilst creating the ward: ${err}`)
		}
	}

	/**
	 * render the form!
	 */
	render() {
		return (
			<div className="row">
				<div className="create-location-input col s12">
					<header><h1>Create a Location</h1></header>
					<form ref={f => this.form = f} className="row">
						<div className="input-field col m6 s12">
							<input id="name" type="text" ref={i => this.name = i} data-length="24" />
							<label htmlFor="name" className="validate">Name</label>
						</div>

						<div className="input-field col s12">
							<textarea id="description" className="materialize-textarea" ref={t => this.desc = t} data-length="120" />
							<label htmlFor="description" className="validate">Description</label>
						</div>
						<div className="col s12">
							<a className="waves-effect waves-light btn" onClick={this.makeWard.bind(this)}>Submit</a>
						</div>
					</form>
				</div>
			</div>
		)
	}
}

export default CreateWard

import {h, Component} from 'preact'
import cloneDeep from 'lodash/cloneDeep'
import M from 'materialize-css'
import Axios from 'axios'

import {Loader} from '../Partial'
import {fhirBase} from '../util'

import '../styles/permissions.scss'

// permissions base, similar to FHIR base, but with a different content-type
export const permissionsBase = Axios.create({
	baseURL: '/',
	headers: {
		accept: 'application/json',
		'content-type': 'application/json',
	},
})

// apply a new token for every request as logging in natively will use old token
permissionsBase.interceptors.request.use((config) => {
	if (localStorage.getItem('token')) {
		config.headers.token = localStorage.getItem('token')
	}
	return config
})


class Permissions extends Component {
	constructor() {
		super()
		this.state = {
			practitioners: [], // practitioner list from API (Bundle resourceType)
			patients: [], // patient list by encounter from API
			selectedPractitioner: null,
			practitionerPermissions: [],
			loaded: false,
		}
		this.permissions = [
			{ident: 'view:allpatients', desc: 'View all patients without any need for assignment'},
			{ident: 'edit:permissions', desc: 'Edit permissions of any practitioner'},
			{ident: 'delete:patients', desc: 'Delete any patient'},
			{ident: 'add:patients', desc: 'Admit a patient'},
			{ident: 'add:wards', desc: 'Create a new ward'},
			{ident: 'add:vitals', desc: 'Add vital signs readings'},
			{ident: 'add:user', desc: 'Add a new user (for non-google login'},
			{ident: 'edit:link', desc: 'Change the link between practitioners and patients'},
		]
		this.renderPermissions = this.renderPermissions.bind(this)
		this.togglePermission = this.togglePermission.bind(this)
		// bind setPractitioner so the returned arrow func has `this` bound to instance of Permissions
		// ensures that if a child component is used, the correct state is updated
		this.setPractitioner = this.setPractitioner.bind(this)
	}

	/**
	 * get practitioner and encounter information from server. update state for lists
	 */
	async componentDidMount() {
		const [{data: practitioners}, {data: patients}] = await Promise.all([
			fhirBase.get('/Practitioner'),
			fhirBase.get('/Encounter/?class=admission&_include=Encounter:patient;location'),
		])
		this.setState({
			practitioners,
			patients,
			selectedPractitioner: null,
			// deep clone because the method will be acting on the objects underneath
			oldPatients: cloneDeep(patients),
			loaded: true,
		})
	}

	/**
	 * create a function that:
		* pull all data (patient relationships and permissions) from server
		* stores this data in state
	 * @param {number} id practitioner ID (database PK)
	 */
	setPractitioner(id) {
		return async () => {
			// fetch union table from permissions API
			// sadly FHIR has poor support for permissions
			const [{data}, {data: {permissions}}] = await Promise.all([
				permissionsBase.get(`/permissions/relationships/${id}`),
				permissionsBase.get(`/permissions/view/${id}`),
			])
			const patientIDs = data.map(datum => datum.patient_id)
			const patients = cloneDeep(this.state.oldPatients)
			// elems in matches are objects and thus handlded by ref
			// they can be given a 'grouped' attr
			const matches = patients.filter(patient => patientIDs.includes(patient.subject.id))
			matches.map(match => match.grouped = true)
			this.setState({selectedPractitioner: id, patients, practitionerPermissions: permissions})
		}
	}

	/**
	 * take the selected practitioner (set in state)
	 * and a function that creates/removes a row with practitioner id and patient ID
	 * in practitionerpatients table
	 * @param {number} patientID patient ID to group
	 * @param {boolean} grouped whether the practitioner and patient are grouped
	 */
	makeGrouping(patientID, grouped = false) {
		/**
		 * Attempt to POST to /permissions/destroy or /permission/create
		 * depending on whether patient is already grouped
		 * grouped data stored in state, from /permissions/relationships/:id
		 */
		return async () => {
			if (!this.state.selectedPractitioner) return
			const baseUrl = grouped ? 'destroy' : 'create'
			try {
				await permissionsBase.post(`/permissions/${baseUrl}`, {
					patientID,
					practitionerID: this.state.selectedPractitioner,
				})
				const oldID = this.state.selectedPractitioner
				await this.componentDidMount()
				await this.setPractitioner(oldID)()
				M.toast({html: 'Successfully updated patient and practitioner'})
			} catch (err) {
				M.toast({html: 'You do not have access to do this!'})
			}
		}
	}

	/**
	 * attempt to add or remove a practitioner permission
	 * post this to /permissions/toggle as well as practitioner id, permission and their permission set
	 * @param {string} perm permission to add/remove
	 */
	togglePermission(perm) {
		return async () => {
			try {
				console.log({
					practitionerID: this.state.selectedPractitioner,
					permission: perm,
					set: this.state.practitionerPermissions,
				})
				const resp = await permissionsBase.post('/permissions/toggle', {
					practitionerID: this.state.selectedPractitioner,
					permission: perm,
					set: this.state.practitionerPermissions,
				})
				console.log(resp)
				this.setState({practitionerPermissions: resp.data.permissions}, () => {
					M.toast({html: `Successfully changed ${perm}`})
				})
			} catch (err) {
				M.toast({html: 'You do not have permission to do this!'})
			}
		}
	}


	/**
	 * render a list of practitioners
	 * @returns {preact.VNode[]}
	 */
	renderPractitioners() {
		return this.state.practitioners.entry.map(practitioner => (
			<li
				onClick={this.setPractitioner(practitioner.id)}
				key={practitioner.telecom[0].value}
				className={`collection-item hover practitioner ${this.state.selectedPractitioner === practitioner.id ? 'selected' : ''}`}
			>
				<span className="title"><b>Name:</b> {practitioner.name[0].given[0]}</span>
				<p><b>Username:</b> {practitioner.telecom[0].value}</p>
			</li>
		))
	}

	/**
	 * render patient list
	 * @returns {preact.VNode[]}
	 */
	renderPatients() {
		return this.state.patients.map(patient => (
			<li
				key={patient.subject.id}
				className={`collection-item hover patient ${patient.grouped ? 'selected' : ''}`}
				onClick={this.makeGrouping(patient.subject.id, patient.grouped)}
			>
				<span className="title"><b>Name:</b> {patient.subject.name[0].text}</span>
				<p><b>Ward:</b> {patient.location[0].name}</p>
			</li>
		))
	}

	/**
	 * render a permission list based off of component state
	 * @returns {preact.VNode[]}
	 */
	renderPermissions() {
		const perms = this.state.practitionerPermissions
		return this.permissions.map(perm => (
			<li
				key={perm.ident}
				className={`collection-item hover ${perms.includes(perm.ident) ? 'selected' : ''}`}
				onClick={this.togglePermission(perm.ident)}
			>
				<span className="title"><b>Permission: </b>{perm.ident}</span>
				<p><b>Description: </b>{perm.desc}</p>
			</li>
		))
	}

	/**
	 * render patient permissions page.
	 * render loader is no patient information loaded yet
	 * @returns {preact.VNode}
	 */
	render() {
		if (!this.state.loaded) return <Loader />
		return (
			<div className="row permissions-table">
				<div className="col s6">
					<h2>Users</h2>
					<ul className="collection">{this.renderPractitioners()}</ul>
				</div>
				<div className="col s6">
					<div className="row">
						<h2>Patients</h2>
						<ul className="collection">{this.renderPatients()}</ul>
					</div>
					<div className="row">
						<h2>Edit permissions</h2>
						<ul className="collection">{this.renderPermissions()}</ul>
					</div>
				</div>
			</div>
		)
	}
}

export default Permissions

