class SearchPatient extends Component {
	/**
	 * render a search field
	 * @param {preact.ComponentProps} props component props
	 */
	constructor(props) {
		super(props)
		this.state = {
			patients: [],
		}
		this.search = this.search.bind(this)
	}

	/**
	 * clear searchbox when page closed/changed
	 */
