	 * Create a fhir OperationOutcome object literal
	 * @returns {object} fhir object literal
	 */
	get outcome() {
		return {
			resourceType: 'OperationOutcome',
			issue: [{
				severity: this.sev,
				code: this.code,
				details: {
					text: this.infoText,
				},
				diagnostics: this.diagnostics,
				expression: this.fhirPath,
			}],
		}
	}

	/**
	 * Make a HTTP response
	 * @returns {void}
	 */
