const diagnosticRouter = require('express').Router()

const {client, knex} = require('../db')
const {createOutcome} = require('./util')
const log = require('../logger')
const DiagnosticReport = require('./classes/DiagnosticReport')
const Observation = require('./classes/Observation')


// return all DiagnosticReports
diagnosticRouter.get('/', async (req, res) => {
	// pull query params for db query
	const {
		patient: patient_id,	// patient ID
		result,					// link results (bool)
		_count,					// number of reports to send
		page,					// which page of results
	} = req.query
	const offset = _count * page

	// pull reports from postgres
	const rows = await knex('diagnostic_report')
		.select()
		.where({patient_id})
		.limit(_count)
		.offset(offset)

	const reports = await Promise.all(
		rows
			.map(row => new DiagnosticReport(row))
			.sort((a, b) => {
				const aDate = new Date(a.last_updated)
				const bDate = new Date(b.last_updated)
				if (aDate > bDate) return -1
				if (aDate < bDate) return 1
				return 0
			})
			.map((report) => {
				if (result) return report.fhirLinked()
				return Promise.resolve(report.fhir())
			}),
	)
	res.json(reports)
})

// get a specific report
diagnosticRouter.get('/:id', async (req, res) => {
	const {id} = req.params
	const {rows: [row]} = await client.query({
		text: 'SELECT * FROM diagnostic_report WHERE report_id = $1',
		values: [id],
	})
	const obs = new DiagnosticReport(row)
	// pull linked data if speficied
	const resp = req.query.result ? await obs.fhirLinked() : obs.fhir()
	res.json(resp)
})


diagnosticRouter.delete('/:id', async (req, res) => {
	const {id} = req.params
	log.debug(`attempting to delete ${id} from diagnostic_report`, {file: 'fhir/diagnostic-report.js', func: 'DELETE /:id'})
	await client.query({
		name: 'delete-diagnostic-report',
		text: 'DELETE FROM diagnostic_report WHERE report_id = $1',
		values: [id],
	})
	createOutcome(req, res, 200, 'Successfully deleted', {}, 'success')
})

diagnosticRouter.post('/', async (req, res) => {
	const expectedObs = [
		'respiratory_rate',
		'oxygen_saturation',
		'supplemental_oxygen',
		'body_temperature',
		'systolic_bp',
		'heart_rate',
		'level_of_consciousness',
		'patient_id',
	]
	const validRequest = expectedObs.filter(obs => !(obs in req.body))
	if (validRequest.length) return createOutcome(req, res, 400, 'Missing data', validRequest)
	const patID = req.body.patient_id
	delete req.body.patient_id
	const observations = Object.keys(req.body)
		.filter(key => expectedObs.includes(key))
		.map(key => new Observation(key, req.body[key]))
	const queries = observations.map(({query}) => query)

	const idList = (await Promise.all(queries.map(query => client.query(query))))
		.map(resp => resp.rows[0])
		.reduce((acc, cur) => {
			acc[cur.name] = cur.observation_id
			return acc
		}, {})
	const now = new Date()
	const keys = ['last_updated', 'patient_id', ...Object.keys(idList)]
	const rows = keys.join(', ')
	const dolla = keys.map((_, idx) => `$${idx + 1}`).join(', ')
	const query = {
		name: 'create-report',
		text: `INSERT INTO diagnostic_report (${rows}) VALUES (${dolla}) RETURNING report_id`,
		values: [now, patID, ...Object.values(idList)],
	}
	const {rows: [row]} = await client.query(query)
	return createOutcome(req, res, 200, 'successfully added observation', row, 'success')
})


module.exports = diagnosticRouter

const encounterRouter = require('express').Router()
const {knex} = require('../db')
const {decodeJWTPayload} = require('../auth/token')
const Encounter = require('./classes/Encounter')
const OperationOutcome = require('./classes/OperationOutcome')

/**
 * Handle all encoutners:
 * 	required as FHIR offers no logical way to store patients
 */

/**
  * Accept a new ancounter
  */
encounterRouter.post('/', async (req, res) => {
	const enc = new Encounter(req.body)
	// attempt to insert the data.
	// inserted will be false if req.body does not have the required fields
	const inserted = await enc.insert()
	const outcome = inserted
		? new OperationOutcome('success', 200, req.originalUrl, 'Successfully added encounter')
		: new OperationOutcome('error', 406, req.originalUrl, 'Unable to add encounter')
	outcome.makeResponse(res)
})


/**
 * find a new encounter
 * this required the practitioner to have view:allpatients to view all
 * else will fall back to practitionerpatients in table
 */
encounterRouter.get('/', async (req, res) => {
	const decodedToken = decodeJWTPayload(req.headers.token)

	// remove _include as we can use knex to search.where(req.query)
	const include = req.query._include
	delete req.query._include

	// queryparams may have _include=patient;encounter
	// split these in to an object so the fhir() method on encounter can populate
	const [, ...tail] = include.split(':')
	const toInclude = tail.join(':').split(';').reduce((acc, cur) => {
		acc[cur] = true
		return acc
	}, {})

	// pull all encounters based on the querystring
	const rows = await knex('encounter').select().where(req.query)

	// if no permission to view all:
	// select all from union table and filter out based on practitonerpatients
	if (!decodedToken.permissions.includes('view:allpatients')) {
		const unionTable = await knex('practitionerpatients')
			.select()
			.where({practitioner_id: decodedToken.userid})

		// map to patient ID and remove them from the earlier rows set
		const patientIDs = unionTable.map(group => group.patient_id)

		// use promise.all to 'concurrently' access the database
		const mapped = await Promise.all(rows
			.filter(row => patientIDs.includes(row.patient_id))
			.map(row => new Encounter(row).fhir(toInclude)))
		res.json(mapped)
		return
	}

	// the user has all permissions, populate these and send to user
	const mapped = await Promise.all(
		rows.map(row => new Encounter(row).fhir(toInclude)),
	)
	res.json(mapped)
})

// get an encounter based on the encounter ID (GET /fhir/Encounter/1)
encounterRouter.get('/:encounter_id', async (req, res) => {
	const {encounter_id} = req.params
	const enc = new Encounter({encounter_id})
